import { useState } from "react";
import { useForm } from "react-hook-form";

function ComponentA() {
    const {
        register,
        handleSubmit,
        reset,
        // formState: { errors },
    } = useForm()
    const [submittedValues, setSubmittedValues] = useState([]);
    const onSubmit = (data) => {
        console.log("submit button is clicked", data);
        setSubmittedValues([...submittedValues, data.title]);
        reset();
    }
    return (
        <>
            <div className="flex justify-center">
                <div className="w-[800px] bg-slate-200">
                    <h1 className="mt-4 font-bold text-2xl">To-Do Form</h1>
                    <div className="flex flex-row justify-center">
                        <form onSubmit={handleSubmit(onSubmit)}>
                            <input type="text" placeholder="title" {...register('title', { required: 'Title is required' })}
                                className="mt-4 w-[400px] h-[44px] px-2 py-1 border border-gray-300
                                placeholder-gray-500 text-gray-700 rounded-md
                                focus:outline-none focus:ring-indigo-500
                                focus:border-indigo-500"></input>
                            {/* <p className="text-red-500 mt-1 text-start text-xs">{errors?.title?.message}</p> */}
                            <button type="submit" className="mt-4 mb-2 ml-2 px-4 h-11 w-[100px] text-white bg-violet-500 hover:bg-violet-600 rounded-md">Submit</button>
                        </form>
                    </div>
                    <div className="flex justify-center">
                        <ul>
                            {submittedValues.map((value, index) => (
                                <>
                                <div className="flex flex-row">
                                <div className="flex items-center justify-center h-10 w-[400px] bg-slate-500"  key={index}> 
                                <p className="">{value}</p></div>
                                <button type="submit" className=" ml-12 px-4 h-10 w-[40px] text-white bg-violet-500 hover:bg-violet-600 rounded-md"></button>
                                <button type="submit" className=" ml-2 px-4 h-10 w-[40px] text-white bg-violet-500 hover:bg-violet-600 rounded-md"></button>

                                </div>
                                </>

                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </>
    );
}

export default ComponentA
