import { useState } from "react";
import { useForm } from "react-hook-form";

function ComponentA() {
    const {
        register,
        handleSubmit,
        reset,
        formState: { errors },
    } = useForm()
    const [submittedValues, setSubmittedValues] = useState([]);
    const onSubmit = (data) => {
        console.log("submit button is clicked", data);
        setSubmittedValues([...submittedValues, data.title]);
        reset();
    }
    return (
        <>
            <div>To-Do Form</div>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input type="text" placeholder="title" {...register('title')}
                    className="mt-10 w-[400px] h-[44px] px-2 py-1 border border-gray-300
                                placeholder-gray-500 text-gray-700 rounded-md
                                focus:outline-none focus:ring-indigo-500
                                focus:border-indigo-500"></input>
                <button type="submit" className="mt-4 mb-2 px-5 h-12 w-full text-white bg-violet-500 hover:bg-violet-600 rounded-md">Submit</button>
            </form>
            <div>
                <ul>
                    {submittedValues.map((value, index) => (
                        <p key={index}>{value}</p>
                    ))}
                </ul>
            </div>
        </>
    );
}

export default ComponentA
