import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { TiTickOutline } from "react-icons/ti";
import { AiOutlineDelete } from "react-icons/ai";
import { MdOutlineEdit } from "react-icons/md";

function ComponentA() {
    const {
        register,
        handleSubmit,
        reset,
        // formState: { errors },
    } = useForm()
    const [submittedValues, setSubmittedValues] = useState([]);
    const [editIndex, setEditIndex] = useState(null); // State to hold the index of the item being edited
    const [editedTitle, setEditedTitle] = useState(""); // State to hold the edited title

    const onSubmit = (data) => {
        console.log("submit button is clicked", data);
        if (editIndex !== null) {
            const updatedValues = [...submittedValues];
            updatedValues[editIndex].title = data.title;
            setSubmittedValues(updatedValues);
            setEditIndex(null); // Reset editIndex after editing
        } else {
            setSubmittedValues([...submittedValues, { title: data.title, crossed: false }]);
        }
        reset();
    };

    const handleEdit = (index) => {
        setEditIndex(index); // Enable editing for the clicked item
        setEditedTitle(submittedValues[index].title); // Set the edited title
    };

    const handleCross = (index) => {
        const updatedValues = [...submittedValues];
        updatedValues[index].crossed = !updatedValues[index].crossed;
        setSubmittedValues(updatedValues);
    };

    const handleDelete = (index) => {
        const updatedValues = [...submittedValues];
        updatedValues.splice(index, 1);
        setSubmittedValues(updatedValues);
    };

    return (
        <>
            <div className="flex justify-center">
                <div className="w-[800px] bg-slate-200">
                    <h1 className="mt-4 font-bold text-2xl">To-Do Form</h1>
                    <div className="flex flex-row justify-center">
                        <form onSubmit={handleSubmit(onSubmit)}>
                            <input
                                type="text"
                                placeholder="title"
                                {...register('title', { required: 'Title is required' })}
                                className="mt-4 w-[400px] h-[44px] px-2 py-1 border border-gray-300
                                placeholder-gray-500 text-gray-700 rounded-md
                                focus:outline-none focus:ring-indigo-500
                                focus:border-indigo-500"
                                value={editIndex !== null ? editedTitle : ""} // Set value of input field when editing
                                onChange={(e) => setEditedTitle(e.target.value)} // Update edited title
                            ></input>
                            <button type="submit" className="mt-4 mb-2 ml-2 px-4 h-11 w-[100px] text-white bg-violet-500 hover:bg-violet-600 rounded-md">
                                {editIndex !== null ? "Update" : "Submit"} {/* Change button text based on edit mode */}
                            </button>
                        </form>
                    </div>
                    <div className="flex justify-center">
                        <ul>
                            {submittedValues.map((item, index) => (
                                <div className="flex flex-row mb-2" key={index}>
                                    <div
                                        className={`flex items-center justify-center rounded-md h-10 w-[500px] ${item.crossed ? 'bg-black' : 'bg-slate-500'} text-white ${item.crossed ? 'line-through' : ''}`}
                                        onClick={() => handleEdit(index)} // Enable editing when clicked
                                    >
                                        <p>{item.title}</p>
                                    </div>
                                    <button className="ml-4 h-10 w-[40px] text-white bg-green-500 hover:bg-green-600 rounded-md flex items-center justify-center" onClick={() => handleCross(index)}>
                                        <TiTickOutline className="h-7 w-7"/>
                                    </button>
                                    <button className="ml-4 h-10 w-[40px] text-white bg-red-500 hover:bg-red-600 rounded-md flex items-center justify-center" onClick={() => handleDelete(index)}>
                                        <AiOutlineDelete className="h-6 w-6" />
                                    </button>
                                </div>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </>
    );
}

export default ComponentA;
